BUILD_DIR=win32/
SOURCE_DIR=.
DEPENDENCIES=$(pwd)/..

mkdir -p $BUILD_DIR

cmake -H$SOURCE_DIR -B$BUILD_DIR \
	-DCMAKE_TOOLCHAIN_FILE=$DEPENDENCIES/CMake-Mingw64-Toolchain.cmake \
	-DZLIB_INCLUDE_DIR=$DEPENDENCIES/zlib-1.3.1 \
	-DZLIB_LIBRARY=$DEPENDENCIES/zlib-1.3.1/libz.a \
	-DJPEG_INCLUDE_DIR=$DEPENDENCIES/jpeg-9f \
	-DJPEG_LIBRARY=$DEPENDENCIES/jpeg-9f/libjpeg.a
